/*global niLabviewApp */

(function () {
    'use strict';
    var niLabviewControlModelsModule = angular.module('singleButtonControlModelsModule', []);

    /**************************************************************
     * Service that returns the control models generated by LabVIEW
     **************************************************************/
    niLabviewControlModelsModule.factory('niLabviewControlModels', function () {
        var controlModels =
        {
            // ******************************************
            // *** Section for control initialization
            // *** Init Code
            buttonFunction19:{ width: '75px', height: '30px' },
            // ******************************************
        };

        return controlModels;
    });

    // ******************************************
    // Request remote data service and connect
    // ******************************************
    niLabviewApp.run(function (niLabviewDataService) {
        niLabviewDataService.startLocalDataServer();
    });
})();